value.css:
.heading {
  padding-bottom: 14vw;
  background-image: linear-gradient(
45deg
, #e98853, #f07084);
  font-family: Roboto, sans-serif;
  color: transparent;
  font-size: 13vw;
  line-height: 18vw;
  font-weight: 700;
  text-align: center;
  letter-spacing: -1vw;
  text-transform: capitalize;
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
}

.subheading {
  font-family: Roboto, sans-serif;
  color: #262626;
  font-size: 7vw;
  line-height: 9vw;
  font-weight: 500;
  text-align: center;
  letter-spacing: -0.5vw;
  text-transform: capitalize;
}

.button {
    text-align: center;
    display: block;
    width: fit-content;
    margin: 2vw auto 0;
    border-radius: 4vw;
    padding: 30px 20px;
    background-color: #e98853;
    color: #fff;
    font-size: 4vw;
    font-weight: 400;
}

