styles.raw:
.primo-page {
  opacity: 0.9;
  background-color: #fefefe;
}
.heading, .subheading, .button {
  font-family: 'Ubuntu', sans-serif;
}
.gradient {
  color: transparent;
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
}
.outer {
  margin-top: 3vw;
}
.button {
  text-align: center;
  display: block;
  width: fit-content;
  padding: 1vw 2vw;
  font-size: 2vw;
  margin-left: auto;
  margin-right: auto;
  margin-top: 2vw;
  background-color: #5380e9;
}

.button:hover {
  background-color: #7153e9;
}

.body {
  color: #444;
  font-size: 2.75vw;
  line-height: 1.1;
  font-weight: 300;
}

h3 {
  font-weight: 800;
}

.heading, .subheading {
  opacity: 0;
  transform: translateY(20vh);
  visibility: hidden;
  transition: opacity 0.6s ease-out, transform 1.2s ease-out;
  will-change: opacity, visibility;
}
.heading.is-visible, .subheading.is-visible {
  opacity: 1;
  transform: none;
  visibility: visible;
}

@media (max-width: 768px) {
  .heading .subheading {
    text-align: center;  
  }
  .button {
    font-size: 5vw;
    padding: 2vw 4vw;
  }
  .heading {
    margin-bottom: 10vw;
  }
  .subheading {
    margin-bottom: 8vw;
  }
  .body {
    font-size: 5vw;
    text-align: center;
  }  
}

